#include<bits/stdc++.h>
using namespace std;
int main()
{
	long long n,k,time=1;
	cin>>n>>k;
	if(n<0)
		cout<<"Impossible"<<endl;
	else
	{
		time+=(n-n/(k+1))*2+n/(k+1);
		cout<<time<<endl;
	}
	return 0;
}
